(319, 1, '2015-03-24 16:18:38', '2015-03-24 16:18:38', '<h3>1. General Commands</h3>\r\n<table class="run-commands" width="375">\r\n<tbody>\r\n<tr>\r\n<td width="196">Calculator</td>\r\n<td width="179">calc</td>\r\n</tr>\r\n<tr>\r\n<td>Command Prompt</td>\r\n<td>cmd</td>\r\n</tr>\r\n<tr>\r\n<td>Control Panel</td>\r\n<td>control</td>\r\n</tr>\r\n<tr>\r\n<td>Firefox</td>\r\n<td>firefox</td>\r\n</tr>\r\n<tr>\r\n<td>Internet Explorer</td>\r\n<td>iexplore</td>\r\n</tr>\r\n<tr>\r\n<td>Log Off</td>\r\n<td>logoff</td>\r\n</tr>\r\n<tr>\r\n<td>Microsoft Excel</td>\r\n<td>excel</td>\r\n</tr>\r\n<tr>\r\n<td>Microsoft Word</td>\r\n<td>winword</td>\r\n</tr>\r\n<tr>\r\n<td>Notepad</td>\r\n<td>notepad</td>\r\n</tr>\r\n<tr>\r\n<td>Program Files</td>\r\n<td>%programfiles%</td>\r\n</tr>\r\n<tr>\r\n<td>Registry</td>\r\n<td>regedit</td>\r\n</tr>\r\n<tr>\r\n<td>Restart</td>\r\n<td>shutdown -r</td>\r\n</tr>\r\n<tr>\r\n<td>Shutdown</td>\r\n<td>shutdown</td>\r\n</tr>\r\n<tr>\r\n<td>Task Manager</td>\r\n<td>taskmgr</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3>2. System Tools</h3>\r\n<table class="run-commands" width="375">\r\n<tbody>\r\n<tr>\r\n<td width="196">Check Disk Utility</td>\r\n<td width="179">chkdsk</td>\r\n</tr>\r\n<tr>\r\n<td>Clipboard Viewer</td>\r\n<td>clipbrd</td>\r\n</tr>\r\n<tr>\r\n<td>Component Services</td>\r\n<td>dcomcnfg</td>\r\n</tr>\r\n<tr>\r\n<td>Computer Management</td>\r\n<td>compmgmt.msc</td>\r\n</tr>\r\n<tr>\r\n<td>Device Manager</td>\r\n<td>devmgmt.msc</td>\r\n</tr>\r\n<tr>\r\n<td>DirectX Diagnostic Tool</td>\r\n<td>dxdiag</td>\r\n</tr>\r\n<tr>\r\n<td>Disk Defragment</td>\r\n<td>dfrg.msc</td>\r\n</tr>\r\n<tr>\r\n<td>Disk Management</td>\r\n<td>diskmgmt.msc</td>\r\n</tr>\r\n<tr>\r\n<td>Disk Partition Manager</td>\r\n<td>diskpart</td>\r\n</tr>\r\n<tr>\r\n<td>Event Viewer</td>\r\n<td>eventvwr.msc</td>\r\n</tr>\r\n<tr>\r\n<td>Group Policy Editor</td>\r\n<td>gpedit.msc</td>\r\n</tr>\r\n<tr>\r\n<td>Local Security Settings</td>\r\n<td>secpol.msc</td>\r\n</tr>\r\n<tr>\r\n<td>Local Users and Groups</td>\r\n<td>lusrmgr.msc</td>\r\n</tr>\r\n<tr>\r\n<td>Performance Monitor</td>\r\n<td>perfmon</td>\r\n</tr>\r\n<tr>\r\n<td>Services</td>\r\n<td>services.msc</td>\r\n</tr>\r\n<tr>\r\n<td>Shared Folders</td>\r\n<td>fsmgmt.msc</td>\r\n</tr>\r\n<tr>\r\n<td>System Configuration Editor</td>\r\n<td>sysedit</td>\r\n</tr>\r\n<tr>\r\n<td>System Configuration Utility</td>\r\n<td>msconfig</td>\r\n</tr>\r\n<tr>\r\n<td>System Properties</td>\r\n<td>sysdm.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Windows Transfer Wizard</td>\r\n<td>migwiz</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3>3. Control Panel</h3>\r\n<table class="run-commands" width="375">\r\n<tbody>\r\n<tr>\r\n<td width="196">Accessibility Controls</td>\r\n<td width="179">access.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Add Hardware Wizard</td>\r\n<td>hdwwiz.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Add/Remove Programs</td>\r\n<td>appwiz.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Administrative Tools</td>\r\n<td>control admintools</td>\r\n</tr>\r\n<tr>\r\n<td>Automatic Updates</td>\r\n<td>wuaucpl.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Date and Time</td>\r\n<td>timedate.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Display Properties</td>\r\n<td>control desktop, desk.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Folders Properties</td>\r\n<td>control folders</td>\r\n</tr>\r\n<tr>\r\n<td>Fonts Folder</td>\r\n<td>fonts</td>\r\n</tr>\r\n<tr>\r\n<td>Internet Properties</td>\r\n<td>inetcpl.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Keyboard Properties</td>\r\n<td>control keyboard</td>\r\n</tr>\r\n<tr>\r\n<td>Mouse Properties</td>\r\n<td>control mouse, mouse.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Network Connections</td>\r\n<td>control netconnections, ncpa.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Network Setup Wizard</td>\r\n<td>netsetup.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>On-Screen Keyboard</td>\r\n<td>osk</td>\r\n</tr>\r\n<tr>\r\n<td>Power Configuration</td>\r\n<td>powercfg.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Printers and Faxes</td>\r\n<td>control printers</td>\r\n</tr>\r\n<tr>\r\n<td>Regional Settings</td>\r\n<td>intl.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Scanners and Cameras</td>\r\n<td>sticpl.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Security Center</td>\r\n<td>wscui.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Sounds and Audio</td>\r\n<td>mmsys.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>System</td>\r\n<td>control system</td>\r\n</tr>\r\n<tr>\r\n<td>User Accounts</td>\r\n<td>control userpasswords</td>\r\n</tr>\r\n<tr>\r\n<td>Windows Firewall</td>\r\n<td>firewall.cpl</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3>4. System Folders</h3>\r\n<table class="run-commands" width="375">\r\n<tbody>\r\n<tr>\r\n<td width="196">Application Data Folder</td>\r\n<td width="179">%appdata%</td>\r\n</tr>\r\n<tr>\r\n<td>Home Directory</td>\r\n<td>%homepath%</td>\r\n</tr>\r\n<tr>\r\n<td>Home Directory Drive</td>\r\n<td>%homedrive%</td>\r\n</tr>\r\n<tr>\r\n<td>My Documents [XP]</td>\r\n<td>userinit, my documents</td>\r\n</tr>\r\n<tr>\r\n<td>Temporary Folder</td>\r\n<td>%temp%</td>\r\n</tr>\r\n<tr>\r\n<td>Windows Root Directory</td>\r\n<td>%windir%</td>\r\n</tr>\r\n<tr>\r\n<td>Windows Root Drive</td>\r\n<td>%systemdrive%</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3>5. Advanced Commands</h3>\r\n<h4>System Properties Tabs</h4>\r\n<table class="run-commands" width="375">\r\n<tbody>\r\n<tr>\r\n<td>Computer Name</td>\r\n<td>control sysdm.cpl,,1</td>\r\n</tr>\r\n<tr>\r\n<td>Hardware</td>\r\n<td>control sysdm.cpl,,2</td>\r\n</tr>\r\n<tr>\r\n<td>Advanced</td>\r\n<td>control sysdm.cpl,,3</td>\r\n</tr>\r\n<tr>\r\n<td>System Restore</td>\r\n<td>control sysdm.cpl,,4</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h4>Display Properties Tabs</h4>\r\n<table class="run-commands" width="375">\r\n<tbody>\r\n<tr>\r\n<td style="text-align: center;" colspan="2"><strong>Windows XP</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Themes</td>\r\n<td>desk.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Desktop</td>\r\n<td>control desk.cpl,,0</td>\r\n</tr>\r\n<tr>\r\n<td>Screen Saver</td>\r\n<td>control desk.cpl,,1</td>\r\n</tr>\r\n<tr>\r\n<td>Appearance</td>\r\n<td>control desk.cpl,,2</td>\r\n</tr>\r\n<tr>\r\n<td>Settings</td>\r\n<td>control desk.cpl,,3</td>\r\n</tr>\r\n<tr>\r\n<td style="text-align: center;" colspan="2"><strong>Windows 7</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Screen Resolution</td>\r\n<td>desk.cpl</td>\r\n</tr>\r\n<tr>\r\n<td>Desktop Icon Settings</td>\r\n<td>control desk.cpl,,0</td>\r\n</tr>\r\n<tr>\r\n<td>Screen Saver</td>\r\n<td>control desk.cpl,,1</td>\r\n</tr>\r\n<tr>\r\n<td>Personalization</td>\r\n<td>control desk.cpl,,2</td>\r\n</tr>\r\n<tr>\r\n<td>Window Color and Appearance</td>\r\n<td>control desk.cpl,,5</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h4>Other Commands</h4>\r\n<table class="run-commands" width="375">\r\n<tbody>\r\n<tr>\r\n<td>Resultant set of group policies</td>\r\n<td>rsop.msc</td>\r\n</tr>\r\n<tr>\r\n<td>User Accounts Advanced</td>\r\n<td>control userpasswords2</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3>6. Windows 7 only</h3>\r\n<h4>6.1. System</h4>\r\n<table class="run-commands" width="783">\r\n<tbody>\r\n<tr>\r\n<td width="394">Environment Variables Window</td>\r\n<td width="389">rundll32 sysdm.cpl,EditEnvironmentVariables</td>\r\n</tr>\r\n<tr>\r\n<td colspan="2">  </td>\r\n</tr>\r\n<tr>\r\n<td>Folder Options</td>\r\n<td>rundll32 shell32.dll,Options_RunDLL 0</td>\r\n</tr>\r\n<tr>\r\n<td>Folder Options, View tab</td>\r\n<td>rundll32 shell32.dll,Options_RunDLL 7</td>\r\n</tr>\r\n<tr>\r\n<td>Folder Options, Search tab</td>\r\n<td>rundll32 shell32.dll,Options_RunDLL 2</td>\r\n</tr>\r\n<tr>\r\n<td colspan="2">  </td>\r\n</tr>\r\n<tr>\r\n<td>Notification Area Icon, System Icons</td>\r\n<td>rundll32 shell32.dll,Options_RunDLL 4</td>\r\n</tr>\r\n<tr>\r\n<td>Notification Area Icons</td>\r\n<td>rundll32 shell32.dll,Options_RunDLL 5</td>\r\n</tr>\r\n<tr>\r\n<td colspan="2">  </td>\r\n</tr>\r\n<tr>\r\n<td>System Properties, Advanced</td>\r\n<td>SystemPropertiesAdvanced</td>\r\n</tr>\r\n<tr>\r\n<td>System Properties, Computer Name</td>\r\n<td>SystemPropertiesComputerName</td>\r\n</tr>\r\n<tr>\r\n<td>System Advanced, Performance Options</td>\r\n<td>SystemPropertiesPerformance</td>\r\n</tr>\r\n<tr>\r\n<td>System Properties, Protection</td>\r\n<td>SystemPropertiesProtection</td>\r\n</tr>\r\n<tr>\r\n<td colspan="2">  </td>\r\n</tr>\r\n<tr>\r\n<td>Taskbar and Start Menu Properties</td>\r\n<td>control /name Microsoft.TaskbarandStartMenu</td>\r\n</tr>\r\n<tr>\r\n<td>Taskbar and Start Menu Properties</td>\r\n<td>rundll32 shell32.dll,Options_RunDLL 1</td>\r\n</tr>\r\n<tr>\r\n<td>Taskbar and Start Menu Properties, Start Menu tab</td>\r\n<td>rundll32 shell32.dll,Options_RunDLL 3</td>\r\n</tr>\r\n<tr>\r\n<td>Taskbar and Start Menu Properties, Toolbars tab</td>\r\n<td>rundll32 shell32.dll,Options_RunDLL 6</td>\r\n</tr>\r\n<tr>\r\n<td colspan="2">  </td>\r\n</tr>\r\n<tr>\r\n<td>Turn Windows features on or off</td>\r\n<td>optionalfeatures</td>\r\n</tr>\r\n<tr>\r\n<td colspan="2">  </td>\r\n</tr>\r\n<tr>\r\n<td>User Accounts Advanced</td>\r\n<td>netplwiz</td>\r\n</tr>\r\n<tr>\r\n<td>User Account Control Settings</td>\r\n<td>UserAccountControlSettings</td>\r\n</tr>\r\n<tr>\r\n<td>User Profiles</td>\r\n<td>rundll32 sysdm.cpl,EditUserProfiles</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h4>6.2. Folders</h4>\r\n<table class="run-commands" width="783">\r\n<tbody>\r\n<tr>\r\n<td width="394">Documents Folder</td>\r\n<td width="389">documents</td>\r\n</tr>\r\n<tr>\r\n<td>Downloads Folder</td>\r\n<td>downloads</td>\r\n</tr>\r\n<tr>\r\n<td>Favorites Folder</td>\r\n<td>favorites</td>\r\n</tr>\r\n<tr>\r\n<td>Pictures Folder</td>\r\n<td>pictures</td>\r\n</tr>\r\n<tr>\r\n<td>Recent Folder</td>\r\n<td>recent</td>\r\n</tr>\r\n<tr>\r\n<td>User libraries (Documents, Pictures etc)</td>\r\n<td>userinit</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h4>6.3. Other Commands</h4>\r\n<table class="run-commands" width="783">\r\n<tbody>\r\n<tr>\r\n<td width="394">Display Properties (change text size)</td>\r\n<td width="389">dpiscaling</td>\r\n</tr>\r\n<tr>\r\n<td>Display Switch</td>\r\n<td>displayswitch</td>\r\n</tr>\r\n<tr>\r\n<td>Sound Volume</td>\r\n<td>sndvol.exe</td>\r\n</tr>\r\n<tr>\r\n<td>Sticky Notes</td>\r\n<td>stikynot</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>', '\'Run\' Window Commands', '', 'publish', 'open', 'open', '', 'run-window-commands', '', '', '2015-03-26 08:35:54', '2015-03-26 08:35:54', '', 0, 'http://pcadvice.co.nf/?p=319', 0, 'post', '', 0),
(326, 1, '2015-03-24 17:10:17', '0000-00-00 00:00:00', '<table width="372">\n<tbody>\n<tr>\n<td width="203">Calculator</td>\n<td width="169">calc</td>\n</tr>\n<tr>\n<td>Command Prompt</td>\n<td>cmd</td>\n</tr>\n<tr>\n<td>Control Panel</td>\n<td>control</td>\n</tr>\n<tr>\n<td>Firefox</td>\n<td>firefox</td>\n</tr>\n<tr>\n<td>Internet Explorer</td>\n<td>iexplore</td>\n</tr>\n<tr>\n<td>Log Off</td>\n<td>logoff</td>\n</tr>\n<tr>\n<td>Microsoft Word</td>\n<td>winword</td>\n</tr>\n<tr>\n<td>Microsoft Excel</td>\n<td>excel</td>\n</tr>\n<tr>\n<td>Notepad</td>\n<td>notepad</td>\n</tr>\n<tr>\n<td>Paint</td>\n<td>mspaint</td>\n</tr>\n<tr>\n<td>Program Files</td>\n<td>%programfiles%</td>\n</tr>\n<tr>\n<td>Registry</td>\n<td>regedit</td>\n</tr>\n<tr>\n<td>Restart</td>\n<td>shutdown -r</td>\n</tr>\n<tr>\n<td>Shutdown</td>\n<td>shutdown</td>\n</tr>\n<tr>\n<td>Task Manager</td>\n<td>taskmgr</td>\n</tr>\n</tbody>\n</table>\n&nbsp;', '', '', 'draft', 'open', 'open', '', '', '', '', '2015-03-24 17:10:17', '2015-03-24 17:10:17', '', 0, 'http://pcadvice.co.nf/?p=326', 0, 'post', '', 0),
