(585, 1, '2015-03-30 20:07:33', '0000-00-00 00:00:00', '<p>3. Create another file <strong>custom.js</strong> in the same folder dd</p>\n<pre class="code-js">( function( global, $ ) {\n  var editor,\n  syncCSS = function() {\n    $(\'#custom_css_textarea\').val( editor.getSession().getValue() );\n  },\n  loadAce = function() {\n    editor = ace.edit( \'custom_css_div\' );\n    \n    global.safecss_editor = editor;\n    editor.getSession().setUseWrapMode( true );\n    editor.getSession().setValue( $( \'#custom_css_textarea\' ).val() );\n    editor.getSession().setMode("ace/mode/css");\n    \n    $( \'#options_form\' ).submit( syncCSS );\n    editor.focus();\n  };\n    \n  $( global ).load( loadAce );\n  global.aceSyncCSS = syncCSS;\n} )( this, jQuery );</pre>\n<h3>Prism Test</h3>\n<pre class="code-css">.main-navigation .menu-item-has-children &gt; a:after,\n.main-navigation .page_item_has_children &gt; a:after {\n    -webkit-font-smoothing: antialiased;\n    display: inline-block;\n    font-family: \'Fontawesome\';\n    position: absolute;\n    right: 1.2rem;\n    top: 1.7rem;\n    vertical-align: text-bottom;\n}\n.main-navigation .menu-item-has-children &gt; a:after,\n.main-navigation .page_item_has_children &gt; a:after {\n    content: "\\f0d7";\n}</pre>', 'Prism Test', '', 'draft', 'open', 'open', '', '', '', '', '2015-03-30 20:07:33', '2015-03-30 20:07:33', '', 0, 'http://pcadvice.co.nf/?p=585', 0, 'post', '', 0),
